import Connection from "./Api";

class UserService {
  async register(userData) {
    let res = null;
    await Connection.post("/register", userData).then((response) => {
      if (response.status === 201) {
        alert("SUCCESSFUL REGISTRATION.");
      }
      res = response;
    });
    return res;
  }

  async login(userData) {
    let res = null;
    await Connection.get("/login", userData).then((response) => {
      res = response;
    });
    return res;
  }

  async authenticate(userData) {
    let res = null;
    await Connection.get("/authenticate", userData).then((response) => {
      res = response;
    });
    return res;
  }

  async getUsers() {
    let res = null;
    await Connection.get("/users").then((response) => {
      res = response;
    });
    return res;
  }
}

export default UserService;
