import Connection from "./Api";

class UserService {
  async register(user) {
    await Connection.post("/register", user).then((response) => {
      return response;
    });
  }

  async getUser(id) {
    const data = await Connection.get(`/users/${id}`).then((response) => {
      return response.data;
    });
    return data;
  }

  async getUsers() {
    let res = null;
    await Connection.get("/users").then((response) => {
      res = response;
    });
    return res;
  }
}

export default UserService;
