import React from "react";
import { render, unmountComponentAtNode } from "react-dom";
import AlertBox from "./AlertBox";

export default function alertBox(statusCode = 200, message, duration = 6000) {
  let semiRandomId = `${Math.random()}-${Math.random()}-${Math.random()}`;
  alertContainer = document.getElementById("alert");
  if (alertContainer) {
    render(
      <AlertBox
        id={semiRandomId}
        statusCode={statusCode}
        message={message}
        hideAlert={() => hideAlert(semiRandomId)}
      />
    );
    setTimeout(() => {
      hideAlert(semiRandomId);
    }, duration);
  }
}

const hideAlert = (id) => {
  const alertBox = document.getElementById(id);
  if (alertBox) unmountComponentAtNode(document.getElementById("alert"));
};
