import React, { useEffect, useState } from "react";
import Day from "./Day";
import { FaArrowRight } from "react-icons/fa";
import "../Components.css";

export default function Habit({ title }) {
  const [hasLoaded, setHasLoaded] = useState(null);
  const [streak, setStreak] = useState(null);

  useEffect(() => {
    if (!hasLoaded) {
      setStreak([3, 2, 1, 3, 2, 1, 0]);
      setHasLoaded(true);
    }
  }, [streak, hasLoaded]);

  return (
    <div style={{ padding: 10 }}>
      <div
        style={{
          padding: "10px 0",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        {title}
        <FaArrowRight />
      </div>
      <div style={{ display: "flex" }}>
        {streak
          ? streak.map((s) => {
              return <Day value={s} />;
            })
          : null}
      </div>
    </div>
  );
}
