import React from "react";
import { weekdays, months } from "../../../Global/Constants";
import { FaEllipsisH } from "react-icons/fa";

export default function Post({ data }) {
  return (
    <div style={{ border: "1px solid grey", margin: "10px 0", padding: 10 }}>
      <div style={{ fontSize: "14px", color: "grey", display: "flex" }}>
        <div style={{ flex: 1 }}>
          {weekdays[data.date.weekday]}, {data.date.day}{" "}
          {months[data.date.month]}
        </div>
        <FaEllipsisH />
      </div>
      <div style={{ fontSize: "20px", marginTop: "10" }}>{data.content}</div>
    </div>
  );
}
