import Api from "./Api";

class UserService {
  async register(userData) {
    let registered = false;
    await Api.post("/register", userData).then((response) => {
      if (response.status === 201) {
        registered = true;
      }
    });
    return registered;
  }

  async authorize() {
    let authorized = false;
    Api.post(
      "/authorize",
      {},
      {
        headers: {
          Authorization: "Bearer " + localStorage.access_token, //the token is a variable which holds the token
        },
      }
    ).then((response) => {
      if (response.status === 200) authorized = true;
    });
    return authorized;
  }

  async login(userData) {
    let authenticated = false;
    await Api.post("/login", userData).then((response) => {
      if (response.status === 200) {
        alert(JSON.stringify(response));
        localStorage.access_token = response.data.access_token;
        authenticated = true;
      }
    });
    return authenticated;
  }

  async getUsers() {
    let res = null;
    await Api.get("/users").then((response) => {
      res = response;
    });
    return res;
  }
}

export default UserService;
