import React from "react";
import { weekdays, months } from "../../../Global/Constants";
import { FaEllipsisH } from "react-icons/fa";

export default function Post({ data }) {
  const d = new Date();
  const year = d.getFullYear();
  const month = d.getMonth();
  const day = d.getDate();
  const dayDiff = day - data.date.day;

  let dateString = "";

  if (year === data.date.year && month === data.date.month && dayDiff > 20) {
    dateString = `${d.getHours() - data.date.hours}h`;
  } else {
    dateString = `${weekdays[data.date.weekday]}, ${data.date.day}
          ${months[data.date.month]}`;
  }

  return (
    <div style={{ border: "1px solid grey", margin: "10px 0", padding: 10 }}>
      <div style={{ fontSize: "14px", color: "grey", display: "flex" }}>
        <div style={{ flex: 1 }}>{dateString}</div>
        <FaEllipsisH />
      </div>
      <div style={{ fontSize: "20px", padding: "10px 0 15px 0" }}>
        {data.content}
      </div>
    </div>
  );
}
