import React, { useState, useEffect } from "react";
import PostService from "../../Shared/PostService";
import { AiFillEdit, AiFillDelete } from "react-icons/ai";
import "./Blog.css";
import "../../Styles/Buttons.css";

export default function Posts() {
  const [postInput, setPostInput] = useState("");
  const [postId, setPostId] = useState(null);
  const [posts, setPosts] = useState(null);
  const [postsFetched, setPostsFetched] = useState(false);

  const postService = new PostService();

  const submitPost = (e) => {
    e.preventDefault();
    postService.createPost({ content: postInput });
    getPosts();
  };
  const getPosts = () => {
    postService.getPosts().then((posts) => {
      setPosts(posts);
      setPostsFetched(true);
    });
  };
  const editPost = () => {};
  const deletePost = () => {};

  const write = (e) => {
    setPostInput(e.target.value);
  };

  useEffect(() => {
    if (!postsFetched) {
      getPosts();
    }
    return () => {};
  }, []);

  return (
    <div className="postScroll">
      <form className="post marginY" onSubmit={submitPost}>
        <textarea
          onBlur={write}
          placeholder="Capture a Feeling, Articulate a Thought..."
        ></textarea>
        {/* <div className="post inputDisplay">{input}</div> */}
        <button id="postButton" className="altButton">
          Publish
        </button>
      </form>
      {posts
        ? posts.map((post) => {
            return (
              <div className="post marginY">
                <div className="content">{post.content}</div>
                <div style={{ display: "flex", flexDirection: "column" }}>
                  <button className="postIconButton">
                    <AiFillDelete />
                  </button>
                  <button className="postIconButton">
                    <AiFillEdit />
                  </button>
                </div>
              </div>
            );
          })
        : null}
    </div>
  );
}
