import React, { useState } from "react";
import { Switch, Route, NavLink as Link } from "react-router-dom";
import PostService from "../../Shared/PostService";
import Foods from "./Foods";
import "./Questionnaire.css";

export default function Questionnaire() {
  const postService = new PostService();
  const [page, setPage] = useState(1);
  const [feeling, setFeeling] = useState(1);
  const [factors, setFactors] = useState([]);

  const pick = (factor) => {
    if (!factors.includes(factor)) {
      addFactor(factor);
    } else {
      removeFactor(factor);
    }
  };

  const addFactor = (value) => {
    let factorsCopy = [...factors];
    factorsCopy.push(value);
    setFactors(factorsCopy);
  };

  const removeFactor = (value) => {
    let factorsCopy = [...factors];
    const index = factorsCopy.indexOf(value);
    if (index > -1) {
      factorsCopy.splice(index, 1);
    }
    setFactors(factorsCopy);
  };

  const submitPost = () => {
    // feeling 1 for good, 0 for bad
    postService.createPost(feeling, factors);
  };

  return (
    <div className="questionnaire">
      <header>Let's figure out why you feel ___!</header>
      <div className="grid">
        <Switch>
          <Route exact path="/feel/0">
            <h1>Mood was here before</h1>
          </Route>
          <Route path="/feel/1">
            <Foods factors={factors} pick={pick} />
          </Route>
          <Route path="/feel/2">
            <h1>Activites</h1>
          </Route>
          <Route path="/feel/3">
            <h1>Other</h1>
          </Route>
        </Switch>
      </div>
      <p>{JSON.stringify(feeling)}</p>
      <p>{JSON.stringify(factors)}</p>
      <button onClick={submitPost}>SUBMIT</button>
    </div>
  );
}

const intoxicants = [
  "caffeine",
  "alcohol",
  "marijuana",
  "cigarettes",
  "medication",
  "other",
];

const otherFactors = [
  "Nut",
  "LateNightMeal",
  "Stress",
  "Boredom",
  "Inactivity",
];

