import React, { useState } from "react";
import UserService from "../../Shared/UserService";

export default function Login({ goToSignup, setAuth }) {
  const userService = new UserService();
  const [userData, setUserData] = useState({
    email: localStorage.email,
    password: "",
  });

  const handleLogin = (event) => {
    event.preventDefault();
    if (userData.password.length > 0) {
      userService.login(userData).then((responseCode) => {
        console.log(responseCode);
        if (responseCode === 200) {
          setAuth(true);
          alert("Welcome!");
        } else if (responseCode === 401) {
          alert("Wrong password!");
        } else if (responseCode === 403) {
          alert("No user with that email!");
        }
      });
    } else {
      alert("Enter your password.");
    }
  };

  const handleWrite = (event) => {
    setUserData({
      ...userData,
      [event.target.name]: event.target.value,
    });
  };

  const className = "margin-5 padding-10";

  return (
    <div>
      <h1>Log in</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          name="email"
          placeholder="email"
          value={userData.email}
          autoComplete="off"
          className={className}
          onChange={handleWrite}
        />
        <input
          type="password"
          name="password"
          placeholder="password"
          autoComplete="off"
          className={className}
          onChange={handleWrite}
        />
        <button type="submit" className={className + " submitButton"}>
          Log in
        </button>
        <button onClick={goToSignup} className={className}>
          Sign up
        </button>
      </form>
    </div>
  );
}
