import React, { useEffect, useState } from "react";
import Day from "./Day";

export default function Habit({ title }) {
  const [hasLoaded, setHasLoaded] = useState(null);
  const [streak, setStreak] = useState(null);

  useEffect(() => {
    if (!hasLoaded) {
      setStreak([3, 2, 1, 3, 2, 1, 0]);
      setHasLoaded(true);
    }
  }, [streak, hasLoaded]);

  return (
    <div style={{ padding: 10, border: "1px solid grey" }}>
      <div>{title}</div>
      <div style={{ display: "flex" }}>
        {streak
          ? streak.map((s) => {
              return <Day value={s} />;
            })
          : null}
      </div>
    </div>
  );
}
