import React, { useState } from "react";
import UserService from "../../Shared/UserService";

export default function Login({ goToSignup, setAuth }) {
  const userService = new UserService();
  const [userData, setUserData] = useState({
    email: "",
    password: "",
  });

  const handleLogin = (event) => {
    event.preventDefault();
    userService.login(userData).then((response) => {
      if (response === true) {
        setAuth(true);
      } else {
        alert("Wrong Credentials!");
      }
    });
  };

  const handleWrite = (event) => {
    setUserData({
      ...userData,
      [event.target.name]: event.target.value,
    });
  };

  const className = "margin-5 padding-10";

  return (
    <div>
      <h1>Log in</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          name="email"
          placeholder="email"
          autoComplete="off"
          className={className}
          onChange={handleWrite}
        />
        <input
          type="password"
          name="password"
          placeholder="password"
          autoComplete="off"
          className={className}
          onChange={handleWrite}
        />
        <button type="submit" className={className + " submitButton"}>
          Log in
        </button>
        <button onClick={goToSignup} className={className}>
          Sign up
        </button>
      </form>
    </div>
  );
}
