import React, { useState } from "react";
import InputHeader from "./InputHeader";
import PostService from "../../Shared/PostService";
import "./Questionnaire.css";

export default function Questionnaire() {
  const postService = new PostService();
  const [feeling, setFeeling] = useState(1);
  const [factors, setFactors] = useState([]);

  const pick = (factor) => {
    if (!factors.includes(factor)) {
      addFactor(factor);
    } else {
      removeFactor(factor);
    }
  };

  const addFactor = (value) => {
    let factorsCopy = [...factors];
    factorsCopy.push(value);
    setFactors(factorsCopy);
  };

  const removeFactor = (value) => {
    let factorsCopy = [...factors];
    const index = factorsCopy.indexOf(value);
    if (index > -1) {
      factorsCopy.splice(index, 1);
    }
    setFactors(factorsCopy);
  };

  const submitPost = () => {
    // feeling 1 for good, 0 for bad
    postService.createPost(feeling, factors);
  };

  return (
    <div className="grid">
      <InputHeader />
      {foods.map((food) => {
        return (
          <div
            style={factors.includes(food) ? { background: "blue" } : null}
            onClick={() => pick(food)}
          >
            {food}
          </div>
        );
      })}
      <button onClick={submitPost}>SUBMIT</button>
      <p>{JSON.stringify(factors)}</p>
    </div>
  );
}

const foods = [
  "Sugar",
  "Gluten",
  "Non-gluten Grains",
  "Rice",
  "Dairy",
  "Meat",
  "Fish",
  "Nuts",
  "Fruit",
  "Veggies",
  "Caffeine",
  "Legumes",
  "Eggs",
  "Dairy",
];

const intoxicants = [
  "caffeine",
  "alcohol",
  "marijuana",
  "cigarettes",
  "medication",
  "other",
];

const otherFactors = [
  "Nut",
  "LateNightMeal",
  "Stress",
  "Boredom",
  "Inactivity",
];

