import React from "react";
import { render, unmountComponentAtNode } from "react-dom";
import AlertBox from "./AlertBox";

export default function alertBox(statusCode = 200, message, duration = 5000) {
  let semiRandomId = `${Math.random()}-${Math.random()}-${Math.random()}`;
  render(
    <AlertBox
      id={semiRandomId}
      statusCode={statusCode}
      message={message}
      hideAlert={() => hideAlert(semiRandomId)}
    />,
    document.getElementById("alert")
  );
  setTimeout(() => {
    hideAlert(semiRandomId);
  }, duration);
}

const hideAlert = (id) => {
  document.getElementById(id).classList.toggle("display");
};
