import Api from "./Api";

class UserService {
  // returns only response status code
  async signup(userData) {
    let res = null;
    await Api.post("/signup", userData)
      .then((response) => {
        if (response.status === 201) {
          sessionStorage.accessToken = response.data.accessToken;
          localStorage.refreshToken = response.data.refreshToken;
          localStorage.email = userData.email;
          res = 201;
        }
      })
      .catch((error) => {
        res = error;
      });
    return res;
  }

  // returns only response status code while saving tokens to localStorage
  async login(userData) {
    let res = null;
    await Api.post("/login", userData)
      .then((response) => {
        if (response.status === 200) {
          res = 200;
          localStorage.accessToken = response.data.accessToken;
          localStorage.refreshToken = response.data.refreshToken;
          localStorage.email = userData.email;
        }
      })
      .catch((error) => {
        res = error.response.status;
      });
    return res;
  }

  // returns only response status code
  async authorize() {
    let res = null;
    await Api.post(
      "/authorize",
      {},
      {
        headers: {
          Authorization: "Bearer " + localStorage.accessToken, //the token is a variable which holds the token
        },
      }
    )
      .then((response) => {
        res = response;
      })
      .catch((error) => {
        res = error.response;
      });
    return res;
  }

  // returns the new accessToken or null
  async getRefreshToken() {
    let result = null;
    await Api.post(
      "/getRefreshToken",
      {},
      {
        headers: {
          refreshToken: "Bearer " + localStorage.refreshToken, //the token is a variable which holds the token
        },
      }
    )
      .then((response) => {
        if (response.status === 200) {
          result = 200;
          localStorage.accessToken = response.data.accessToken;
          localStorage.refreshToken = response.data.refreshToken;
        }
      })
      .catch((error) => {
        result = error.response.status;
      });
    return result;
  }

  // returns the new accessToken or null
  async getAccessToken() {
    let accessToken = null;
    await Api.post(
      "/getAccessToken",
      {},
      {
        headers: {
          Authorization: "Bearer " + localStorage.refreshToken, //the token is a variable which holds the token
        },
      }
    )
      .then((response) => {
        if (response.status === 200) {
          sessionStorage.accessToken = accessToken;
        }
      })
      .catch((error) => {
        console.log(error);
      });
    return accessToken;
  }
}

export default UserService;
