import Api from "./Api";

class UserService {
  async signup(userData) {
    let res = null;
    await Api.post("/signup", userData)
      .then((response) => {
        if (response.status === 201) {
          sessionStorage.accessToken = response.data.accessToken;
          localStorage.refreshToken = response.data.refreshToken;
          localStorage.email = userData.email;
          res = 201;
        }
      })
      .catch((error) => {
        res = error;
      });
    return res;
  }

  async login(userData) {
    let didLogin = false;
    await Api.post("/login", userData)
      .then((response) => {
        if (response.status === 200) {
          didLogin = true;
          sessionStorage.accessToken = response.data.accessToken;
          localStorage.refreshToken = response.data.refreshToken;
          localStorage.email = userData.email;
        }
      })
      .catch((error) => {
        console.log(error);
      });
    return didLogin;
  }

  async getRefreshToken() {
    let success = null;
    await Api.post(
      "/getRefreshToken",
      {},
      {
        headers: {
          Authorization: "Bearer " + localStorage.refreshToken,
        },
      }
    )
      .then((response) => {
        if (response.status === 201) {
          sessionStorage.accessToken = response.data.accessToken;
          localStorage.refreshToken = response.data.refreshToken;
          success = true;
        }
      })
      .catch((error) => {
        console.log(error);
      });
    return success;
  }

  async getAccessToken() {
    let success = null;
    await Api.post(
      "/getAccessToken",
      {},
      {
        headers: {
          Authorization: "Bearer " + localStorage.refreshToken,
        },
      }
    )
      .then((response) => {
        if (response.status === 201) {
          sessionStorage.accessToken = response.data.accessToken;
          success = true;
        }
      })
      .catch((error) => {
        console.log(error);
      });
    return success;
  }
}

export default UserService;
