import Api from "./Api";

class UserService {
  // returns only response status code
  async register(userData) {
    let res = null;
    await Api.post("/register", userData)
      .then((response) => {
        if (response.status === 201) {
          this.login(userData).then((res) => {
            res = 200;
          });
        }
      })
      .catch((error) => {
        res = error;
      });
    return res;
  }

  // returns only response status code while saving tokens to localStorage
  async login(userData) {
    let res = null;
    await Api.post("/login", userData)
      .then((response) => {
        if (response.status === 200) {
          res = 200;
          localStorage.access_token = response.data.access_token;
          localStorage.refresh_token = response.data.refresh_token;
          localStorage.email = userData.email;
        }
      })
      .catch((error) => {
        res = error.response.status;
      });
    return res;
  }

  // returns only response status code
  async authorize() {
    let res = null;
    await Api.post(
      "/authorize",
      {},
      {
        headers: {
          Authorization: "Bearer " + localStorage.access_token, //the token is a variable which holds the token
        },
      }
    )
      .then((response) => {
        res = response;
      })
      .catch((error) => {
        res = error.response;
      });
    return res;
  }

  // returns the new accessToken or null
  async renewAccessToken() {
    let newAccessToken = null;
    await Api.post(
      "/renewAccessToken",
      {},
      {
        headers: {
          refreshToken: "Bearer " + localStorage.refresh_token, //the token is a variable which holds the token
        },
      }
    )
      .then((response) => {
        if (response.status === 200) {
          console.log("renewAccessToken()");
          console.log(response);
          // newAccessToken =
        }
      })
      .catch((error) => {
        console.log(error);
      });
    return newAccessToken;
  }
}

export default UserService;
