import Connection from "./Connection";

class CustomerService {
  async getCustomer(customerId) {
    const data = await Connection.get(`/customer/${customerId}`).then(
      (response) => {
        return response.data;
      }
    );
    return data;
  }

  async registerCustomer(customer) {
    await Connection.post(`/customer`, customer).then((response) => {
      alert("registerCustomer " + response.data);
    });
  }

  async getCustomers() {
    let customers = [];
    const data = await Connection.get("/customer").then((response) => {
      return response.data;
    });
    data.forEach((customer) => {
      customers.push(customer);
    });
    return customers;
  }
}

export default CustomerService;
