import React, { useState, useContext } from "react";
import UserService from "../../Shared/UserService";
import dialog from "../../Components/Alert/ShowAlert";
import { AuthContext } from "../../Contexts/AuthContext";

export default function Login({ goToSignup }) {
  const { setAuth } = useContext(AuthContext);

  const userService = new UserService();
  const [userData, setUserData] = useState({
    email: localStorage.email,
    password: "",
  });

  const handleLogin = async (event) => {
    event.preventDefault();
    if (!(userData.password.length > 0 && userData.email.length > 0)) {
      dialog(400, "Please enter both fields.");
    } else {
      await userService.login(userData).then((response) => {
        if (response === 200) {
          dialog(200, "Welcome back!");
          setAuth(true);
        } else if (response === 401) {
          dialog(401, "Wrong password.");
        } else if (response === 403) {
          dialog(403, "No user with that email.");
        } else {
          dialog(400, "Unkown error.");
        }
      });
    }
  };

  const handleWrite = (event) => {
    setUserData({
      ...userData,
      [event.target.name]: event.target.value,
    });
  };

  const className = "margin-5 padding-10";

  return (
    <div>
      <h1>Log in</h1>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          name="email"
          placeholder="Email"
          value={userData.email}
          autoComplete="off"
          className={className}
          onChange={handleWrite}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          autoComplete="off"
          className={className}
          onChange={handleWrite}
        />
        <button type="submit" className={className + " submitButton"}>
          Log in
        </button>
        <button onClick={goToSignup} className={className}>
          Sign up
        </button>
      </form>
    </div>
  );
}
